mapper.update:
update warehousemanage w
set w.warename    = #{wareName},
    w.address     = #{address},
    w.update_time = #{updateTime}
where id = #{id}

update warehousemanage w
set w.loss_number = #{lossNumber},
    w.w_number      = #{number},
    w.update_time = #{updateTime}
where id = #{id}

update warehousemanage w
set w.sale_number = #{saleNumber},
    w.w_number      = #{number},
    w.update_time = #{updateTime}
where id = #{id}

update warehousemanage w
set w.w_number      = #{number},
    w.update_time = #{updateTime}
where id = #{id}

mapper.select:
select w.*,wp.w_name,p.product_name FROM warehousemanage w left join product p on p.id = w.product_id left join warehouse_parent wp on wp.id = w.parent_id where w.is_delete = 0
AND w.warename LIKE concat('%', #{wareName}, '%')
order by w.create_time desc limit #{start},#{end}

select *
from warehousemanage
where product_id = #{productId}
  and is_delete = 0;
